mapper.update:
update center_img
set
PHOTO_KND =#{photoKnd},
PHOTO_NO = #{photoNo},
PHOTO_URL = #{photoUrl}
WHERE animal_no = #{animal_no} and PHOTO_NO = #{photoNo}

mapper.select:
		SELECT * FROM (
        SELECT c.*, ROW_NUMBER() OVER (ORDER BY ANIMAL_NO DESC) AS rnum
        FROM center c
   	 	)
   		 WHERE rnum BETWEEN #{start} + 1 AND #{start} + #{limit}
